class AlbumsAPI(Resource):
  def get(self):
    p = int(request.args.get('page', 1))
    if p == 1:
      return { 'data': map(make_public_url, albumsInPage1 ), 'has_more': True }
    elif p == 2:
      return { 'data': map(make_public_url, albumsInPage2 ), 'has_more': False }
    else:
      abort(404)

